svg.script:
var clippedImage, clippingCircle, lensFrame, isMouseDown = false, scaleFactor = 3;

function mouseMove(evt) {
    if(!isMouseDown) return;
    var x = evt.clientX;
    var y = evt.clientY;
    clippingCircle.setAttribute("cx", x);
    clippingCircle.setAttribute("cy", y);
    lensFrame.setAttribute("cx", x);
    lensFrame.setAttribute("cy", y);
    resizeImage(x,y);
}

function mouseDown(evt) {
    isMouseDown = true;
    document.getElementById("percentageText").firstChild.nodeValue = "" + Math.round(scaleFactor*100) + "%";
}

function mouseUp(evt) {
    isMouseDown = false;
}

function resizeImage(x, y) {   
    clippedImage.setAttribute("transform", 
        "translate(" + (x - scaleFactor*x) + "," + (y - scaleFactor*y) + "),scale(" + scaleFactor + "," + scaleFactor +")");
}

window.addEventListener("mousedown", mouseDown);
window.addEventListener("mousemove", mouseMove);
window.addEventListener("mouseup", mouseUp);

window.onload = function() {
    clippedImage = document.getElementById("clippedImage");
    clippingCircle = document.getElementById("clippingCircle");
    lensFrame = document.getElementById("lensFrame");
}
